mapper.insert:
{call insert_user_ps(
#{username, mode=IN, jdbcType=VARCHAR},
#{password, mode=IN, jdbcType=VARCHAR},
#{name, mode=IN, jdbcType=VARCHAR},
#{email, mode=IN, jdbcType=VARCHAR},
#{userId, mode=OUT, jdbcType=INTEGER}
)}

mapper.select:
select ut.user_id, ut.username, ut.password, ut.name, ut.email,
urt.user_role_id, urt.user_id as urt_user_id, urt.role_id as urt_role_id,
rt.role_id, rt.role_name from user_tb ut left outer join user_role_tb urt on (urt.user_id = ut.user_id) left outer join role_tb rt on (rt.role_id = urt.role_id) where ut.username = #{username}

select ut.user_id, ut.username, ut.password, ut.name, ut.email,
urt.user_role_id, urt.user_id as urt_user_id, urt.role_id as urt_role_id,
rt.role_id, rt.role_name from user_tb ut left outer join user_role_tb urt on (urt.user_id = ut.user_id) left outer join role_tb rt on (rt.role_id = urt.role_id) where ut.user_id = #{userId}
